import React from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import homeIcon from '../image/SystemIcon.svg';
import backIcon from '../image/SyIcon.svg';

function Detail_Header() {
  const navigate = useNavigate();
  const location = useLocation();

  const goBack = () => {
    navigate(-1); // 이전 페이지로 이동
  }

  const headerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '10px',
    borderBottom: '1px solid #ddd',
    position: 'fixed', // 고정 위치 설정
    width: '100%', // 너비를 100%로 설정
    backgroundColor: '#fff', // 배경색을 흰색으로 설정
    zIndex: 1, // 다른 요소 위에 위치하도록 설정
  };

  const footerStyle = {
    width: '100%',
    position: 'fixed',
    bottom: 0, // 페이지의 맨 아래에 위치하도록 설정
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '10px',
    backgroundColor: '#fff', // 배경색을 흰색으로 설정
  };

  const support = () => {
    alert("응원하기 버튼이 클릭되었습니다!");
    // 이 곳에 응원하기 버튼 클릭 시 수행할 동작을 정의하세요.
  };

  return (
    <>
      <div style={headerStyle}>
        <button onClick={goBack}><img src={backIcon} alt="홈 아이콘" width="24" height="24" /></button>
        
        <Link to="/">
        <img src={homeIcon} alt="홈 아이콘" width="24" height="24" />
        </Link>
      </div>

      <div style={footerStyle}>
        <button onClick={support} style={{width: '100%', height: 40, paddingLeft: 10, paddingRight: 10, paddingBottom: 5,background: '#00C4C4', borderRadius: 4, justifyContent: 'center', alignItems: 'center', gap: 6, display: 'inline-flex', border: 'none'}}>
          <div style={{textAlign: 'center', color: 'white', fontSize: 14, fontFamily: 'Pretendard', fontWeight: '400', lineHeight: 20, wordWrap: 'break-word'}}>응원하기</div>
        </button>
      </div>
    </>
  )
}

export default Detail_Header;
